background: white;
}

/* Create two unequal columns that floats next to each other */
/* Left column */
.leftcolumn {
float: left;
width: 75%;
}

/* Right column */
.rightcolumn {
float: left;
width: 25%;
padding-left: 20px;
}

/* Fake image */
.fakeimg {
background-color: #aaa;
width: 100%;
padding: 20px;
}

/* Add a card effect for articles */
.card {
background-color: white;
padding: 20px;
margin-top: 20px;
}

/* Clear floats after the columns */
.row:after {
content: "";
display: table;
clear: both;
}

/* Footer */
.footer {
padding: 20px;
text-align: center;
background: #ddd;
margin-top: 20px;
}

/* Responsive layout - when the screen is less than 800px wide, make the two columns stack on top of each other instead of next to each other */
@media screen and (max-width: 800px) {
.leftcolumn, .rightcolumn {
width: 100%;
padding: 0;
}
}

/* When the input field receives input, convert the value from pounds to kilograms */
function weightConverter(valNum) {
document.getElementById("outputGrams").innerHTML = valNum / 0.0022046;
}

/* When the input field receives input, convert the value from fahrenheit to celsius */
function temperatureConverter(valNum) {
valNum = parseFloat(valNum);
document.getElementById("outputCelsius").innerHTML = (valNum-32) / 1.8;
}

/* When the input field receives input, convert the value from feet to meters */
function lengthConverter(valNum) {
document.getElementById("outputMeters").innerHTML = valNum / 0.0022046;
}

/* When the input field receives input, convert the value from mph to kph */
function speedConverter(valNum) {
valNum = parseFloat(valNum);
document.getElementById("outputKPH").innerHTML = valNum * 1.609344;
}
