def binarySearch(arr, targetVal):
left = 0
right = len(arr) - 1

while left <= right:
mid = (left + right) // 2

if arr[mid] == targetVal:
return mid

if arr[mid] < targetVal:
left = mid + 1
else:
right = mid - 1

return -1

